mapper.select:
select
IF((select
count(user_acct)
from t_sys_user
where user_acct = #{userAcct}

    and user_id != #{userId}
)!=0,1,0)
+
IF((select
count(phone)
from t_sys_user
where phone = #{phone}

    and user_id != #{userId}
)!=0,2,0)
+
IF((select
count(invite_code)
from t_info_client
where invite_code = #{inviteCode}
)=0,4,0)
